import React, { useEffect, useState } from "react";
import {images} from "./mocks";
import { loadImagesApi } from "./api";
import Gallery from "./components/Gallery";
import Modal from "./components/Modal";

const App = () => {
  const [gallery, setGallery] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);

  const fetchImages = async () => {
    try {
      const data = await loadImagesApi(images);
      setGallery(data);
    } catch (e) {
      console.log(e.message);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const onImageSelected = (selectedImage) => {
    setSelectedImage(selectedImage);
  }

  const onModalClose = () => {
    setSelectedImage(null);
  }

  return (
    <div className="image-container">
      <Gallery onImageSelected={onImageSelected} gallery={gallery}/>
      {selectedImage && (
        <Modal onModalClose={onModalClose} selectedImage={selectedImage} />
      )}
    </div>
  );
};

export default App;
